<? if (!defined("B_PROLOG_INCLUDED") || B_PROLOG_INCLUDED !== true) die(); ?>
<? $this->setFrameMode(true); ?>
<? if ($arResult["ITEMS"]) : ?>
    <? global $arTheme;
    $bHideOnNarrow = $arTheme['BIGBANNER_HIDEONNARROW']['VALUE'] === 'Y'; ?>
    <div class="top_slider_wrapp maxwidth-banner<?= ($bHideOnNarrow ? ' hidden_narrow' : '') ?>">
        <? $APPLICATION->AddHeadScript(SITE_TEMPLATE_PATH . '/js/jquery.flexslider-min.js', true) ?>
        <div class="swiper-container main-slider-home">
            <div class="swiper-wrapper">
                <div class="swiper-slide">
                    <?
                    $background = is_array($arResult["ITEMS"][0]["DETAIL_PICTURE"]) ? $arResult["ITEMS"][0]["DETAIL_PICTURE"]["SRC"] : $this->GetFolder() . "/images/background.jpg";
                    $mobileItem = CFile::GetPath($arResult["ITEMS"][0]["PROPERTIES"]["MOBILE_IMAGE"]["VALUE"]);
                    $mobileImage = is_array($arResult["ITEMS"][0]["PROPERTIES"]["MOBILE_IMAGE"]) ? $mobileItem : $arResult["ITEMS"][0]["DETAIL_PICTURE"]["SRC"];
                    $target = arResult["ITEMS"][0]["PROPERTIES"]["TARGETS"]["VALUE_XML_ID"];
                    ?>
                    <? if ($arResult["ITEMS"][0]["PROPERTIES"]["URL_STRING"]["VALUE"]) : ?>
                        <a class="target" href="<?= $arResult["ITEMS"][0]["PROPERTIES"]["URL_STRING"]["VALUE"] ?>" <?= (strlen($target) ? 'target="' . $target . '"' : '') ?>>
                            <picture>
                                <source class="swiper-lazy" media="(max-width: 650px)" srcset="data:image/png;base64," data-srcset="<?= $mobileImage ?>">
                                <img src="<?= $background ?>">
                            </picture>
                        </a>
                    <? else : ?>
                        <picture>
                            <source class="swiper-lazy" media="(max-width: 650px)" srcset="data:image/png;base64," data-srcset="<?= $mobileImage ?>">
                            <img src="<?= $background ?>">
                        </picture>
                    <? endif; ?>
                </div>
                <? foreach ($arResult["ITEMS"] as $key => $arItem) : ?>
                    <? if ($key === 0) continue; ?>
                    <?
                    $this->AddEditAction($arItem['ID'], $arItem['EDIT_LINK'], CIBlock::GetArrayByID($arItem["IBLOCK_ID"], "ELEMENT_EDIT"));
                    $this->AddDeleteAction($arItem['ID'], $arItem['DELETE_LINK'], CIBlock::GetArrayByID($arItem["IBLOCK_ID"], "ELEMENT_DELETE"), array("CONFIRM" => GetMessage('CT_BNL_ELEMENT_DELETE_CONFIRM')));
                    $background = is_array($arItem["DETAIL_PICTURE"]) ? $arItem["DETAIL_PICTURE"]["SRC"] : $this->GetFolder() . "/images/background.jpg";
                    $mobileItem = CFile::GetPath($arItem["PROPERTIES"]["MOBILE_IMAGE"]["VALUE"]);
                    $mobileImage = is_array($arItem["PROPERTIES"]["MOBILE_IMAGE"]) ? $mobileItem : $arItem["DETAIL_PICTURE"]["SRC"];
                    $target = $arItem["PROPERTIES"]["TARGETS"]["VALUE_XML_ID"];
                    ?>
                    <div class="swiper-slide <?= ($arItem["PROPERTIES"]["TEXT_POSITION"]["VALUE_XML_ID"] ? " " . $arItem["PROPERTIES"]["TEXT_POSITION"]["VALUE_XML_ID"] : " left"); ?>" data-nav_color="<?= ($arItem["PROPERTIES"]["NAV_COLOR"]["VALUE_XML_ID"] ? $arItem["PROPERTIES"]["NAV_COLOR"]["VALUE_XML_ID"] : ""); ?>" id="<?= $this->GetEditAreaId($arItem['ID']); ?>">
                        <? if ($arItem["PROPERTIES"]["URL_STRING"]["VALUE"]) : ?>
                            <a class="target" href="<?= $arItem["PROPERTIES"]["URL_STRING"]["VALUE"] ?>" <?= (strlen($target) ? 'target="' . $target . '"' : '') ?>>
                                <picture>
                                    <source class="swiper-lazy" media="(max-width: 650px)" srcset="data:image/png;base64," data-srcset="<?= $mobileImage ?>">
                                    <img src="<?= $background ?>">
                                </picture>
                            </a>
                        <? else : ?>
                            <picture>
                                <source class="swiper-lazy" media="(max-width: 650px)" srcset="data:image/png;base64," data-srcset="<?= $mobileImage ?>">
                                <img src="<?= $background ?>">
                            </picture>
                        <? endif; ?>
                        <div class="swiper-lazy-preloader swiper-lazy-preloader-white"></div>
                    </div>
                <? endforeach; ?>
            </div>
            <ul class="flex-direction-nav">
                <li class="flex-nav-prev">
                    <a class="flex-prev" href="#">Previous</a>
                </li>
                <li class="flex-nav-next">
                    <a class="flex-next" href="#">Next</a>
                </li>
            </ul>
            <div class="swiper-pagination"></div>
        </div>
    </div>
<? endif; ?>